import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link, useLocation } from 'react-router-dom';
import './theme.css';

// Page imports
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ServicesPage from './pages/ServicesPage';
import ContactPage from './pages/ContactPage';
import PricingPage from './pages/PricingPage';
import BookingPage from './pages/BookingPage';
import GalleryPage from './pages/GalleryPage';
import TestimonialsPage from './pages/TestimonialsPage';
import FaqPage from './pages/FaqPage';
import BlogPage from './pages/BlogPage';
import TeamPage from './pages/TeamPage';

// Navigation component with mobile support
function Navigation() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const location = useLocation();

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Close menu when route changes
  useEffect(() => {
    setMenuOpen(false);
  }, [location]);

  const navLinks = [
    { to: '/', label: 'Home' },
    { to: '/about', label: 'About' },
    { to: '/services', label: 'Services' },
    { to: '/contact', label: 'Contact' },
    { to: '/pricing', label: 'Pricing' },
    { to: '/booking', label: 'Booking' },
    { to: '/gallery', label: 'Gallery' },
    { to: '/testimonials', label: 'Testimonials' },
    { to: '/faq', label: 'FAQ' },
    { to: '/blog', label: 'Blog' },
    { to: '/team', label: 'Team' },
  ];

  return (
    <nav style={styles.nav}>
      <Link to="/" style={styles.navBrand}>
        <span style={styles.brandText}>YogaAustin</span>
      </Link>

      {/* Desktop Navigation */}
      {!isMobile && (
        <div style={styles.navLinks}>
          {navLinks.map(link => (
            <Link key={link.to} to={link.to} style={styles.navLink}>{link.label}</Link>
          ))}
        </div>
      )}

      {/* Mobile Hamburger Button */}
      {isMobile && (
        <button
          onClick={() => setMenuOpen(!menuOpen)}
          style={styles.hamburger}
          aria-label="Toggle menu"
        >
          <span style={{
            ...styles.hamburgerLine,
            transform: menuOpen ? 'rotate(45deg) translateY(6px)' : 'none'
          }} />
          <span style={{
            ...styles.hamburgerLine,
            opacity: menuOpen ? 0 : 1
          }} />
          <span style={{
            ...styles.hamburgerLine,
            transform: menuOpen ? 'rotate(-45deg) translateY(-6px)' : 'none'
          }} />
        </button>
      )}

      {/* Mobile Dropdown Menu */}
      {isMobile && menuOpen && (
        <div style={styles.mobileMenu}>
          {navLinks.map(link => (
            <Link
              key={link.to}
              to={link.to}
              style={styles.mobileNavLink}
              onClick={() => setMenuOpen(false)}
            >
              {link.label}
            </Link>
          ))}
        </div>
      )}
    </nav>
  );
}

function App() {
  return (
    <BrowserRouter>
      <div style={styles.app}>
        <Navigation />
        
        {/* Main Content */}
        <main style={styles.main}>
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/services" element={<ServicesPage />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/pricing" element={<PricingPage />} />
            <Route path="/booking" element={<BookingPage />} />
            <Route path="/gallery" element={<GalleryPage />} />
            <Route path="/testimonials" element={<TestimonialsPage />} />
            <Route path="/faq" element={<FaqPage />} />
            <Route path="/blog" element={<BlogPage />} />
            <Route path="/team" element={<TeamPage />} />
          </Routes>
        </main>
        
        {/* Footer */}
        <footer style={styles.footer}>
          <p>Â© 2026 YogaAustin. All rights reserved.</p>
        </footer>
      </div>
    </BrowserRouter>
  );
}

